const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    cartTotal: 0
};

const updateItem = (ind, books, cartItems, cartTotal, quantity = 1) => {
    let item = cartItems[ind], book = null;
    if(!item)
    {
        book = books.find((book) => book.id === action.payload); 
        item = {id: book.id, title: book.title, amount: 0, price: 0};
    }
    item.amount += quantity;  
    if(quantity < 0 && item.amount === 0)
        return {cartItems: [...cartItems.slice(0, ind), ...cartItems.slice(ind+1)], 
                cartTotal: cartTotal-item.price};

    item.price += book.price * quantity;  

    let cart = {cartTotal: cartTotal+(book.price*quantity)};
    if(ind!==-1)
        cart.cartItems = [...cartItems.slice(0, ind), item, ...cartItems.slice(ind+1)];
    else
        cart.cartItems = [...cartItems, item]; 
    return cart;
}

const creaseItem = (ind, cartItems, cartTotal, quantity = 1) => {
    let item = cartItems[ind];
    item.amount += quantity;  
    if(quantity < 0 && item.amount === 0)
        return {cartItems: [...cartItems.slice(0, ind), ...cartItems.slice(ind+1)], 
                cartTotal: cartTotal-item.price};

    const finalPrice = (item.price/item.amount)*quantity;
    item.price += finalPrice;
    return {
        cartTotal: cartTotal+finalPrice,
        cartItems: [...cartItems.slice(0, ind), item, ...cartItems.slice(ind+1)]
    };
}

const reducer = (state = initialState, action) => {
    switch(action.type)
    {   
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        case 'ADD_BOOK_TO_CART':
        {
            const itemInd = state.cartItems.findIndex((item) => item.id === action.payload);

            return{
                ...state,
                ...updateItem(itemInd, state.books, state.cartItems, state.cartTotal)
            }
        }
        case 'DELETE_ITEM_FROM_CART':
        {
            const itemInd = state.cartItems.findIndex((item) => item.id === action.payload);
            return{
                ...state,
                ...creaseItem(itemInd, state.cartItems, state.cartTotal, -state.cartItems[itemInd].amount)
            }
        }
        case 'INCREASE_ITEM_IN_CART':
        {
            const itemInd = state.cartItems.findIndex((item) => item.id === action.payload);
            return{
                ...state,
                ...creaseItem(itemInd, state.cartItems, state.cartTotal)
            }
        }
        case 'DECREASE_ITEM_IN_CART':
        {
            const itemInd = state.cartItems.findIndex((item) => item.id === action.payload);
            return{
                ...state,
                ...creaseItem(itemInd, state.cartItems, state.cartTotal, -1)
            }
        }
        default:
            return state;
    }
};

export default reducer;
