const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItem: [
        {
            id: 1,
            title: "tru book",
            amount: 2,
            price: 100
        },
        {
            id: 2,
            title: "falsh book",
            amount: 2,
            price: 100
        }
    ],
    cartTotal: 200
};

const reducer = (state = initialState, action) => {
    switch(action.type)
    {
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        default:
            return state;
    }
};

export default reducer;
