const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    cartTotal: 0
};

const reducer = (state = initialState, action) => {
    switch(action.type)
    {   
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        case 'ADD_BOOK_TO_CART':
            const bookId = action.payload;
            const {id, title, price} = state.books.find((book) => book.id === bookId);
            let newItem = {id, title, amount: 1, price};
            const indInCart = state.cartItems.findIndex((item) => item.id === id);
            console.log(newItem);
            console.log(indInCart);
            if(indInCart !== -1)
            {
                newItem = {...state.cartItems[indInCart], 
                            amount: state.cartItems[indInCart].amount+1, 
                            price: state.cartItems[indInCart].price+price }
                return{
                    ...state,
                    cartItems: [...state.cartItems.slice(0, indInCart), newItem, ...state.cartItems.slice(indInCart+1)]
                }
            }
            else
                return{
                    ...state,
                    cartItems: [...state.cartItems, newItem]
                }
        default:
            return state;
    }
};

export default reducer;
