import React from 'react';

import './book-list-item.css';

const BookListItem = ({book}) => {
    const {title, author, imgLink} = book;
    return (
        <div className="container">
            <div className="row" >
                <div className= "col-5">
                    <img src={imgLink} className="img-fluid" alt="Book cover"/>
                </div>
                <div className="col-5">
                    <span>{title}</span>
                    <span>{author}</span>
                </div>
            </div>
        </div>
    )
}

export default BookListItem;
