const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    cartTotal: 0
};

const updateItem = (ind, book, cartItems, cartTotal, flag = 1) => {
    let item = cartItems[ind]; 
    if(!item) item = {}; 
    const {id = book.id, title = book.title, amount = 0, price = 0} = item;
    const newItem = {id, title, amount: amount+(1*flag), price: price+(book.price*flag)};

    let cart = {cartTotal: cartTotal+(book.price*flag)};
    if(ind!==-1)
        cart.cartItems = [...cartItems.slice(0, ind), newItem, ...cartItems.slice(ind+1)];
    else
        cart.cartItems = [...cartItems, newItem]; 
    return cart;
}

const reducer = (state = initialState, action) => {
    let itemInd = null;
    switch(action.type)
    {   
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        case 'ADD_BOOK_TO_CART':
            const bookId = action.payload;
            const book = state.books.find((book) => book.id === bookId);
            itemInd = state.cartItems.findIndex((item) => item.id === book.id);

            const {cartTotal, cartItems} = updateItem(itemInd, book, state.cartItems, state.cartTotal);
            return{
                ...state,
                cartTotal,
                cartItems
            }
        case 'DELETE_ITEM_FROM_CART':
            const itemId = action.payload;
            itemInd = state.cartItems.findIndex((item) => item.id === itemId);
            
            return {
                ...state,
                cartTotal: state.cartTotal - state.cartItems[itemId].price,
                cartItems: [...state.cartItems.slice(0, itemInd), ...state.cartItems.slice(itemInd+1)]
            };
        default:
            return state;
    }
};

export default reducer;
