const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    cartTotal: 0
};

const updateItem = (ind, book, item = {}, flag = 1) => {
    const {id = book.id, title = book.title, amount = amount+(1*flag), price = price+(book.price*flag)} = item;
    const newItem = {id, title, amount, price};

    let cart = {cartTotal: state.cartTotal+price}
    if(ind!==-1)
        cart.cartItems = [...state.cartItems.slice(0, indInCart), newItem, ...state.cartItems.slice(indInCart+1)];
    else
        cart.cartItems = [...state.cartItems, newItem]; 

    return cart;
}

const reducer = (state = initialState, action) => {
    switch(action.type)
    {   
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        case 'ADD_BOOK_TO_CART':
            const bookId = action.payload;
            const book = state.books.find((book) => book.id === bookId);
            const indInCart = state.cartItems.findIndex((item) => item.id === id);

            const {cartTotal, cartItems} = updateItem(indInCart, book,  state.cartItems[indInCart]);
            return{
                ...state,
                cartTotal,
                cartItems
            }
            
        default:
            return state;
    }
};

export default reducer;
