const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    cartTotal: 0
};

const updateItem = (ind, book, cartItems, cartTotal, quantity = 1) => {
    let item = cartItems[ind]; 
    if(!item) item = {}; 
    const {id = book.id, title = book.title, amount = 0, price = 0} = item;
    const newItem = {id, title, amount: amount+quantity, price: price+(book.price*quantity)};

    let cart = {cartTotal: cartTotal+(book.price*quantity)};
    if(ind!==-1)
        cart.cartItems = [...cartItems.slice(0, ind), newItem, ...cartItems.slice(ind+1)];
    else
        cart.cartItems = [...cartItems, newItem]; 
    return cart;
}

const creaseItem = (ind, cartItems, cartTotal, quantity = 1) => {
    let item = cartItems[ind]; 
    if(quantity < 0 && item.amount > 1)
        return {cartItems, cartTotal};
    if(quantity < 0 && item.amount === 1)
        return {cartItems: [...cartItems.slice(0, ind), ...cartItems.slice(ind+1)], 
                cartTotal: cartTotal-item.price};

    const finalPrice = (item.price/item.amount)*quantity;
    item.price += finalPrice;
    item.amount += quantity; 
    return {
        cartTotal: cartTotal+finalPrice,
        cartItems: [...cartItems.slice(0, ind), item, ...cartItems.slice(ind+1)]
    };
}

const reducer = (state = initialState, action) => {
    switch(action.type)
    {   
        case 'FETCH_BOOKS_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS':
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE':
            return{
                ...state,
                books:[],
                loading: false,
                error: action.payload
            }
        case 'ADD_BOOK_TO_CART':
        {
            const bookId = action.payload;
            const book = state.books.find((book) => book.id === bookId);
            const itemInd = state.cartItems.findIndex((item) => item.id === book.id);

            const {cartTotal, cartItems} = updateItem(itemInd, book, state.cartItems, state.cartTotal);
            return{
                ...state,
                cartTotal,
                cartItems
            }
        }
        case 'DELETE_ITEM_FROM_CART':
        {
            const itemId = action.payload;
            const itemInd = state.cartItems.findIndex((item) => item.id === itemId);
            
            const {cartTotal, cartItems} = creaseItem(itemInd, state.cartItems, state.cartTotal, item.amount);
            return{
                ...state,
                cartTotal,
                cartItems
            }
        }
        case 'INCREASE_ITEM_IN_CART':
        {
            const itemId = action.payload;
            const itemInd = state.cartItems.findIndex((item) => item.id === itemId);

            const {cartTotal, cartItems} = creaseItem(itemInd, state.cartItems, state.cartTotal);
            return{
                ...state,
                cartTotal,
                cartItems
            }
        }
        case 'DECREASE_ITEM_IN_CART':
        {
            const itemId = action.payload;
            const itemInd = state.cartItems.findIndex((item) => item.id === itemId);

            const {cartTotal, cartItems} = creaseItem(itemInd, state.cartItems, state.cartTotal, -1);
            return{
                ...state,
                cartTotal,
                cartItems
            }
        }
        default:
            return state;
    }
};

export default reducer;
